	[95] = {	type = [[装备强化]],	title = [[装备强化规则]],	details = [[突破+5:等级上限提升至105]],},
	[96] = {	type = [[装备强化]],	title = [[装备强化规则]],	details = [[突破+6:等级上限提升至125]],},
	[97] = {	type = [[装备强化]],	title = [[装备强化规则]],	details = [[突破+7:等级上限提升至150]],},
	[98] = {	type = [[装备强化]],	title = [[装备强化规则]],	subtitle = [[3.装备回炉]],	details = [[使用精灵石，可以将装备的所有升级材料进行返回，包括：金币、重铸水晶、突破材料装备。]],},
	[99] = {	type = [[装备强化]],	title = [[装备强化规则]],	details = [[回炉后获得1级原装备。同时获得玩家在该装备上曾经使用的80%重铸水晶、80%升级消耗的金币、所有使用过的的突破材料装备。]],},
	[100] = {	type = [[装备强化]],	title = [[装备强化规则]],	details = [[绿装回炉消耗1精灵石。蓝装回炉消耗2精灵石。紫装回炉消耗5精灵石。橙装回炉消耗10精灵石。金装回炉消耗20精灵石。]],},
	[101] = {	type = [[装备强化]],	title = [[装备强化规则]],	subtitle = [[4.装备重铸]],	details = [[玩家36级解锁。使用重铸水晶重置装备的每级成长。每级成长在重铸时有概率提升，也有概率下降。]],},
	[102] = {	type = [[装备强化]],	title = [[装备强化规则]],	details = [[装备共有5个品级：凡品、良品、优品、极品、绝品，分别对应5个装备成长值区间，成长值提升到相应区间时，品级即会提高。]],},
	[103] = {	type = [[装备强化]],	title = [[装备强化规则]],	details = [[品级提升后不会下降。]],},
	[104] = {	type = [[装备强化]],	title = [[装备强化规则]],	subtitle = [[5.符文]],	details = [[每件装备上可以镶嵌4个符文。当前版本开放武器、头盔、戒指和衣服的符文镶嵌。]],},
	[105] = {	type = [[装备强化]],	title = [[装备强化规则]],	details = [[1号形状符文固定增加生命，2号形状符文固定增加攻击，3号形状符文固定增加防御，4号位置符文固定增加技能伤害。]],},
	[106] = {	type = [[装备强化]],	title = [[装备强化规则]],	details = [[每个位置只能镶嵌1个同样形状的符文。]],},
	[107] = {	type = [[装备强化]],	title = [[装备强化规则]],	details = [[符文有1~4星，分布在狩猎场的副本中，越后面的副本掉落的符文星级越高。星级高的符文属性数值更高。]],},
	[108] = {	type = [[装备强化]],	title = [[装备强化规则]],	details = [[5个区域的狩猎场掉落相应的5个套装所属的初始符文。当装备上镶嵌一定数量所属相同套装的符文时，会激活套装属性。（不同星级也可以激活）。相同的套装属性最多同时激活两套。]],},
	[109] = {	type = [[装备强化]],	title = [[装备强化规则]],	details = [[符文使用魔力石升级。初始符文为白色，强化到一定等级后可以改变颜色。]],},
	[110] = {	type = [[装备强化]],	title = [[装备强化规则]],	details = [[升级所需的魔力石数量与等级有关，每次升级时根据当前等级，有固定的成功率。升级失败后会保留当前等级，不会降级。符文升级后，增加属性数值。+3，+6，+9，+12时会改变符文颜色，并且附加一项随机属性。]],},
	[111] = {	type = [[装备强化]],	title = [[装备强化规则]],	details = [[多余的符文可以通过分解，将符文转换成魔力石，给其他符文升级。]],},
	[112] = {	type = [[装备强化]],	title = [[装备强化规则]],	details = [[可以通过符文重生，消耗一定精灵石，返还70%消耗的魔力石。原符文变为初始状态。]],},
	[113] = {	type = [[图鉴]],	title = [[图鉴规则]],	details = [[在图鉴加成中，收集到一个组合内的所有精灵时，该组合即会激活，并增加相应的攻击/生命。可以将收集到的精灵存入图鉴，防止误操作献祭掉哦~]],},
	[114] = {	type = [[冠军之塔]],	title = [[冠军之塔规则]],	subtitle = [[1.冠军之塔]],	details = [[玩家28级解锁。在冠军之塔中，每天可以挑战10名强力玩家。]],},
	[115] = {	type = [[冠军之塔]],	title = [[冠军之塔规则]],	details = [[10级及以上的精灵都能参加活动。]],},
	[116] = {	type = [[冠军之塔]],	title = [[冠军之塔规则]],	details = [[在每天的挑战过程中，精灵的血量和怒气保留，死亡的精灵无法继续上场战斗。]],},
	[117] = {	type = [[冠军之塔]],	title = [[冠军之塔规则]],	details = [[每次战斗胜利后可以获得冠军硬币和金币。并且根据战斗表现可以增加获得的数量。]],},
	[118] = {	type = [[冠军之塔]],	title = [[冠军之塔规则]],	details = [[1、没有角色阵亡 2、敌方最后一个角色被己方主动技能击杀。达成1个条件数量X2，达成2个条件数量X3。]],},
	[119] = {	type = [[冠军之塔]],	title = [[冠军之塔规则]],	details = [[第3、6、9关卡胜利后，会弹出3个BUFF，选择其中一个对我方精灵进行增强。]],},
	[120] = {	type = [[冠军之塔]],	title = [[冠军之塔规则]],	details = [[当第10个关卡被击破时，下一次挑战会进入下一层，面对更加强大的敌人。活动每天挑战一次，并在第二天获得一次重置次数。]],},
	[121] = {	type = [[冠军之塔]],	title = [[冠军之塔规则]],	subtitle = [[2.硬币商店]],	details = [[在冠军之塔中获得的冠军硬币可以在硬币商店中兑换果实、徽章、重铸水晶。商店中的商品每天21点刷新，也可以使用精灵石立即刷新。]],},
	[122] = {	type = [[签到]],	title = [[签到规则]],	details = [[坚持每天签到，可以获得超丰富的奖励哦。部分奖励相应VIP等级的玩家可以领取双倍。签到奖励每个月都会更新。]],},
	[123] = {	type = [[冠军联赛]],	title = [[冠军联赛规则]],	details = [[冠军联赛系统将在玩家等级达到37级时解锁。]],},
	[124] = {	type = [[冠军联赛]],	title = [[冠军联赛规则]],	subtitle = [[1.战斗规则]],	details = [[将于每周一至周六循环进行，每周日停赛。]],},
	[125] = {	type = [[冠军联赛]],	title = [[冠军联赛规则]],	details = [[玩家初始积分为500分，进入联赛界面时，系统将根据当前积分，筛选出3名可挑战玩家。]],},
	[126] = {	type = [[冠军联赛]],	title = [[冠军联赛规则]],	details = [[玩家在挑战前可以查看对手的布阵及精灵情况，从而制定战斗策略。]],},
	[127] = {	type = [[冠军联赛]],	title = [[冠军联赛规则]],	details = [[所有玩家根据积分进行排位，每战胜一位对手，即可获得一定积分。]],},
	[128] = {	type = [[冠军联赛]],	title = [[冠军联赛规则]],	details = [[每场战斗后还可获得一定的声望，声望可用于兑换道具。]],},
	[129] = {	type = [[冠军联赛]],	title = [[冠军联赛规则]],	details = [[每位玩家每天有10次挑战次数，每次挑战后会激活10分钟的冷却时间。]],},
	[130] = {	type = [[冠军联赛]],	title = [[冠军联赛规则]],	details = [[若挑战过程中存在挑战失败的战斗，可通过使用复活功能来重新挑战。]],},
	[131] = {	type = [[冠军联赛]],	title = [[冠军联赛规则]],	details = [[每段赛程结束后，根据排名来发放奖励。]],},
	[132] = {	type = [[冠军联赛]],	title = [[冠军联赛规则]],	subtitle = [[2.联赛分级]],	details = [[周一至周六，系统每天将根据积分对全服玩家进行分组，每30名玩家一组进行跨服竞技。]],},
	[133] = {	type = [[冠军联赛]],	title = [[冠军联赛规则]],	details = [[冠军联赛共设7个级别，玩家初始级别为青铜级，每日同级别的玩家会被分到一组。]],},
	[134] = {	type = [[冠军联赛]],	title = [[冠军联赛规则]],	details = [[每天22:00结算时，每组排名前10的玩家可以进入更高级别，排名11~20的玩家仍然停留在当前级别，每组排名21~30的玩家将会降级，青铜级玩家不会降级。]],},
	[135] = {	type = [[冠军联赛]],	title = [[冠军联赛规则]],	details = [[每周六22:00结算后，所有榜单显示最终结果，玩家无法继续挑战。]],},
	[136] = {	type = [[冠军联赛]],	title = [[冠军联赛规则]],	details = [[周日冠军联赛停赛，各榜单显示周六结算的最终结果。]],},
	[137] = {	type = [[冠军联赛]],	title = [[冠军联赛规则]],	subtitle = [[3.联赛排行]],	details = [[本服：本服玩家在冠军联赛中的积分排行]],},
	[138] = {	type = [[冠军联赛]],	title = [[冠军联赛规则]],	details = [[巅峰：全服玩家在冠军联赛中的积分排行]],},
	[139] = {	type = [[冠军联赛]],	title = [[冠军联赛规则]],	subtitle = [[4.奖励发放]],	details = [[每日22:00结算时按照玩家当前联赛级别结算声望奖励]],},
	[140] = {	type = [[冠军联赛]],	title = [[冠军联赛规则]],	details = [[每周六22:00结算时根据本服排行为玩家发放宝石等奖励。]],},
	[141] = {	type = [[冠军联赛]],	title = [[冠军联赛规则]],	subtitle = [[5.商店]],	details = [[冠军联赛中设有联赛商店，同时显示8项商品，可使用联赛声望兑换道具。道具在每天22点刷新，也可消耗精灵石手动刷新。]],},
	[142] = {	type = [[冠军联赛]],	title = [[冠军联赛规则]],	details = [[可兑换神兽碎片、宝石、重铸水晶。]],},
	[143] = {	type = [[无尽试炼]],	title = [[无尽试炼规则]],	subtitle = [[1.难度分级]],	details = [[共有4个难度的试炼，达到相应等级后解锁。]],},
	[144] = {	type = [[无尽试炼]],	title = [[无尽试炼规则]],	details = [[根据当前队伍的精灵等级和装备等级，可以选择挑战相应的难度。]],},
	[145] = {	type = [[无尽试炼]],	title = [[无尽试炼规则]],	subtitle = [[2.强化BUFF]],	details = [[每一关试炼内有三个小关卡，根据难度从高到低分为英雄、精英、普通，玩家战胜其中一个关卡就可开启下一轮试炼。]],},
	[146] = {	type = [[无尽试炼]],	title = [[无尽试炼规则]],	details = [[英雄：难度最高，战胜后获得3星]],},
	[147] = {	type = [[无尽试炼]],	title = [[无尽试炼规则]],	details = [[精英：难度适中，战胜后获得2星]],},
	[148] = {	type = [[无尽试炼]],	title = [[无尽试炼规则]],	details = [[普通：难度最低，战胜后获得1星]],},
	[149] = {	type = [[无尽试炼]],	title = [[无尽试炼规则]],	details = [[根据每次战斗的表现可以额外获得更多的星星。]],},
	[150] = {	type = [[无尽试炼]],	title = [[无尽试炼规则]],	details = [[如战斗胜利后没有一只精灵死亡，则获得3倍星星。]],},
	[151] = {	type = [[无尽试炼]],	title = [[无尽试炼规则]],	details = [[如战斗胜利后有一只精灵死亡，则获得2倍星星。]],},
	[152] = {	type = [[无尽试炼]],	title = [[无尽试炼规则]],	details = [[如战斗胜利后有1只以上精灵死亡，则星星不加倍。]],},
	[153] = {	type = [[无尽试炼]],	title = [[无尽试炼规则]],	details = [[每3关可以使用星星兑换强化BUFF来提升实力。]],},
	[154] = {	type = [[无尽试炼]],	title = [[无尽试炼规则]],	details = [[如进入之前挑战过的试炼，在战斗前会有额外的星星奖励。额外的星星会计入最终的排行。]],},
	[155] = {	type = [[无尽试炼]],	title = [[无尽试炼规则]],	subtitle = [[3.排行榜]],	details = [[每个玩家结束一次试炼后都可以加入全服的排名，在次日会根据排名结果发放精灵石奖励。]],},
	[156] = {	type = [[无尽试炼]],	title = [[无尽试炼规则]],	details = [[4个难度的试炼有各自的排行榜，根据玩家在当天最好成绩来排序。]],},
	[157] = {	type = [[无尽试炼]],	title = [[无尽试炼规则]],	details = [[优先按照通关关卡数排序，其次按照获得星星数量。]],},
	[158] = {	type = [[公会战]],	title = [[公会战规则]],	subtitle = [[1.赛程]],	details = [[每14天（2周）为一个完整的赛程。]],},
	[159] = {	type = [[公会战]],	title = [[公会战规则]],	details = [[前3天为报名及休整阶段，期间各公会会长或副会长可以进行报名操作。]],},
	[160] = {	type = [[公会战]],	title = [[公会战规则]],	details = [[第4~13天为正赛阶段，期间就看各位如何为公会荣耀而战！]],},
	[161] = {	type = [[公会战]],	title = [[公会战规则]],	details = [[第9天和第14天，根据公会积分排名，分别挑战BOSS获得超多战场硬币奖励~]],},
	[162] = {	type = [[公会战]],	title = [[公会战规则]],	subtitle = [[2.报名]],	details = [[公会等级达到3级以上开启公会战报名。]],},
	[163] = {	type = [[公会战]],	title = [[公会战规则]],	details = [[ 公会战报名期间，会长/副会长可选择是否报名。]],},
	[164] = {	type = [[公会战]],	title = [[公会战规则]],	details = [[每次报名消耗报名玩家2000公会基金。]],},
	[165] = {	type = [[公会战]],	title = [[公会战规则]],	details = [[每次只要有一人报名，该公会内等级在45级以上的玩家即可进入正赛。]],},
	[166] = {	type = [[公会战]],	title = [[公会战规则]],	details = [[报名后不可取消报名。]],},
	[167] = {	type = [[公会战]],	title = [[公会战规则]],	details = [[报名截止至赛程第3天晚上23:00。]],},
	[168] = {	type = [[公会战]],	title = [[公会战规则]],	details = [[公会战开始前，会将战力相近的3个公会匹配到同一个战场。]],},
	[169] = {	type = [[公会战]],	title = [[公会战规则]],	subtitle = [[3.战场说明]],	details = [[战场一共分作25个据点。除正中央的中立据点外，初始状态下，三大公会各拥有一个基地和7个据点。]],},
	[170] = {	type = [[公会战]],	title = [[公会战规则]],	details = [[基地：无法被占领。无法被攻击且无法驻守队伍。]],},
	[171] = {	type = [[公会战]],	title = [[公会战规则]],	details = [[据点：可以被占领，根据所处地形会对该据点上的攻守方的相应属性的精灵有额外的攻击和生命加成（可叠加）,与友方据点相连接的据点中的防守队伍享有额外的生命和攻击加成（可叠加）。]],},
	[172] = {	type = [[公会战]],	title = [[公会战规则]],	details = [[火山对火属性精灵有加成，发电厂对电属性精灵有加成，湖泊对水属性精灵有加成，草原对草属性精灵有加成，山脉对岩属性精灵有加成。平原没有任何加成。]],},
	[173] = {	type = [[公会战]],	title = [[公会战规则]],	details = [[中立据点：可以被占领，初始由NPC驻守。]],},
	[174] = {	type = [[公会战]],	title = [[公会战规则]],	subtitle = [[4.队伍设置]],	details = [[玩家最多可以设置1只攻击队伍和1只防守队伍。]],},
	[175] = {	type = [[公会战]],	title = [[公会战规则]],	subtitle = [[5.正赛说明]],	details = [[正赛每天分为3个小阶段。]],},
	[176] = {	type = [[公会战]],	title = [[公会战规则]],	details = [[8:00~18:00为准备阶段。期间公会会长/副会长可以对与敌方据点相邻的己方据点下达作战指令（默认为攻击指令）。]],},
	[177] = {	type = [[公会战]],	title = [[公会战规则]],	details = [[公会参战会员需要将防守队伍派遣到与敌方据点相邻的己方据点进行驻守。]],},
	[178] = {	type = [[公会战]],	title = [[公会战规则]],	details = [[21:30~22:00为战斗阶段。期间所有公会成员可以对被指定为攻击目标的敌方据点发动攻击。]],},
	[179] = {	type = [[公会战]],	title = [[公会战规则]],	details = [[战斗阶段结束后，对当天赛况进行结算并发放奖励。]],},
	[180] = {	type = [[公会战]],	title = [[公会战规则]],	subtitle = [[6.作战指令说明]],	details = [[攻击：下达攻击指令需要选择1个相邻的敌方据点作为攻击目标，在开战后己方成员即可选择该据点发动进攻。]],},
	[181] = {	type = [[公会战]],	title = [[公会战规则]],	details = [[防守：开战后，设置为防守的据点中的防守队伍享有额外50%的生命和攻击加成。]],},
	[182] = {	type = [[公会战]],	title = [[公会战规则]],	details = [[支援：开战后，设置为支援的据点中的防守队伍会帮助相邻的友方据点进行防守。当相邻的友方据点的防守队伍被消灭时，支援据点会自动派遣防守队伍帮助防守。]],},
	[183] = {	type = [[公会战]],	title = [[公会战规则]],	subtitle = [[7.战斗说明]],	details = [[公会战战斗为自动战斗。]],},
	[184] = {	type = [[公会战]],	title = [[公会战规则]],	details = [[在开战后，每位玩家有6点行动力，每次攻击消耗1点行动力，并且进入4分钟的恢复时间，玩家可以消耗2精灵石/分钟立即恢复。当行动力消耗完时，可以消耗精灵石购买额外的行动力。]],},
	[185] = {	type = [[公会战]],	title = [[公会战规则]],	details = [[防守方每场战斗后血量保留，直到被消灭。]],},
	[186] = {	type = [[公会战]],	title = [[公会战规则]],	details = [[每场战斗后，攻击方不管胜负都会根据本场防守方最终减少的生命值获得战场荣誉。]],},
	[187] = {	type = [[公会战]],	title = [[公会战规则]],	subtitle = [[8.战场头衔]],	details = [[玩家在公会战中获得的战场荣誉不断累加。每天的结算阶段会根据玩家当前的战场荣誉提升或者保持头衔。]],},
	[188] = {	type = [[公会战]],	title = [[公会战规则]],	details = [[目前，拥有高等级头衔在每天的结算阶段可以获得更多的战场硬币哦~在之后的版本还可以获得神秘奖励，尽请期待~]],},
	[189] = {	type = [[公会战]],	title = [[公会战规则]],	subtitle = [[9.结算阶段]],	details = [[战斗阶段结束后，根据最终每个联盟占领的据点数发放积分。普通据点：5，中立据点：20。在本次公会战中，获得的积分不断累加，根据积分进行排名。]],},
	[190] = {	type = [[公会战]],	title = [[公会战规则]],	details = [[根据玩家的头衔等级和当日获得的战场荣誉发放个人奖励（战场硬币），当日获得的战场荣誉越高、头衔等级越高，获得的硬币也越多。根据联盟的积分排名发放联盟奖励（战场硬币和公会资金）]],},
	[191] = {	type = [[公会战]],	title = [[公会战规则]],	subtitle = [[10.战场BOSS]],	details = [[在赛程的第9天和第14天。会在战场上出现BOSS，公会参战玩家可以进行挑战。排名第1的公会可以挑战时空神兽，排名第2的公会可以挑战金心银魂，排名第3的公会可以挑战雷火圣兽。挑战可以获得超多的硬币奖励哦~]],},
	[192] = {	type = [[公会战]],	title = [[公会战规则]],	details = [[同时公会造成的总伤害达到一定数值后全公会参战玩家可以获得额外的硬币奖励。可获得的硬币数量（时空神兽>金心银魂>雷火圣兽）]],},
	[193] = {	type = [[公会战]],	title = [[公会战规则]],	subtitle = [[11.战场商店]],	details = [[可以使用战场硬币从战场商店中兑换奖励。]],},
	[194] = {	type = [[公会战]],	title = [[公会战规则]],	details = [[每天21:00自动刷新战场商店。每次显示8个物品，玩家也可以使用30个精灵石刷新商店物品。VIP等级每天赠送免费刷新次数。]],},
	[195] = {	type = [[公会战]],	title = [[公会战规则]],	subtitle = [[12.Tips]],	details = [[作为战场总指挥的会长/副会长，要多思考如何下达指令来领导成员在战场上披荆斩棘哦~]],},
	[196] = {	type = [[公会战]],	title = [[公会战规则]],	details = [[作为成员的你，也要在战场上多多发挥自己的实力！]],},
	[197] = {	type = [[秘宝]],	title = [[秘宝规则]],	subtitle = [[1.升级]],	details = [[秘宝分为乐器秘宝、缎带秘宝、经验秘宝三类。每只精灵可以携带1个乐器秘宝和1个缎带秘宝。]],},
	[198] = {	type = [[秘宝]],	title = [[秘宝规则]],	details = [[乐器秘宝增加携带精灵的攻击，缎带秘宝增加携带精灵的生命，经验秘宝提供大量升级经验。]],},
	[199] = {	type = [[秘宝]],	title = [[秘宝规则]],	details = [[秘宝可以使用其他秘宝作为经验升级。升级时提升基础属性。]],},
	[200] = {	type = [[秘宝]],	title = [[秘宝规则]],	subtitle = [[2.锻造]],	details = [[蓝色及以上秘宝有附加属性，当携带精灵与附加属性要求的精灵类型匹配时，附加属性即会激活。]],},
	[201] = {	type = [[秘宝]],	title = [[秘宝规则]],	details = [[蓝色及以上秘宝每提升5级可以增加一次锻造机会。锻造时，需要提供一只与秘宝属性/职业相同的精灵作为材料。]],},
	[202] = {	type = [[秘宝]],	title = [[秘宝规则]],	details = [[蓝色秘宝需要5星14资质的精灵。紫色秘宝需要5星15资质的精灵。锻造后，提升秘宝的附加属性。]],},
	[203] = {	type = [[秘宝]],	title = [[秘宝规则]],	subtitle = [[3.回炉]],	details = [[锻造过的秘宝可以使用精灵石进行回炉。回炉后可以返还锻造过程中所有消耗的精灵。锻造后经验保留，可以作为其他秘宝的升级经验（可以提供80%的经验）。]],},
	[204] = {	type = [[游戏厅游戏]],	title = [[游戏厅游戏：21点]],	subtitle = [[游戏币：]],	details = [[在精灵游戏厅使用的货币，仅可以通过充值获得等额的兑换额度，玩家可以使用精灵石兑换成等额的游戏币。玩家拥有的游戏币可以在奖励页面兑换成游戏内资源。]],},
	[205] = {	type = [[游戏厅游戏]],	title = [[游戏厅游戏：21点]],	subtitle = [[游戏规则：]],	details = [[1.玩家每次可以选择10游戏币，或者100游戏币作为赌注]],},
	[206] = {	type = [[游戏厅游戏]],	title = [[游戏厅游戏：21点]],	details = [[2.下注后，玩家可以和系统进行21点游戏，系统作为庄家，牌库为4副。系统和玩家点数相同时，系统赢。]],},
	[207] = {	type = [[游戏厅游戏]],	title = [[游戏厅游戏：21点]],	details = [[3.开局后，玩家和系统各发到2张牌,一张明牌一张暗牌。]],},
	[208] = {	type = [[游戏厅游戏]],	title = [[游戏厅游戏：21点]],	details = [[4.玩家拿到牌后：]],},
	[209] = {	type = [[游戏厅游戏]],	title = [[游戏厅游戏：21点]],	details = [[①没有超过21点，可以选择 继续要牌 、停牌]],},
	[210] = {	type = [[游戏厅游戏]],	title = [[游戏厅游戏：21点]],	details = [[②等于21点，直接停牌]],},
	[211] = {	type = [[游戏厅游戏]],	title = [[游戏厅游戏：21点]],	details = [[③大于21点，直接爆牌]],},
	[212] = {	type = [[游戏厅游戏]],	title = [[游戏厅游戏：21点]],	details = [[5.玩家停牌后，玩家和系统一同开牌，点数大的赢，如爆牌则直接失败。]],},
	[213] = {	type = [[游戏厅游戏]],	title = [[游戏厅游戏：21点]],	details = [[6.玩家赢牌可以获得赌注的双倍奖励，21点获胜可以获得4倍奖励。]],},
	[214] = {	type = [[游戏厅游戏]],	title = [[游戏厅游戏：21点]],	details = [[7.玩家赢牌后可以选择继续或者重新开局，选择继续则将赢得的赌注全部当做下一局的赌注，选择重新开局则收回当前赢得的奖励，重新下注。]],},
	[215] = {	type = [[狩猎场]],	title = [[狩猎场规则]],	subtitle = [[1.宝藏]],	details = [[共有3个宝箱：木质宝箱、黑铁宝箱、水晶宝箱。每天每个宝箱可以开启一次。开启宝箱所需的钥匙数量每天会随机生成。
公会玩家需要从狩猎场中寻找到开启宝箱所需的钥匙，和同公会的其他玩家一同开启宝箱。]],},
	[216] = {	type = [[狩猎场]],	title = [[狩猎场规则]],	details = [[开启宝箱可以获得魔力石、精灵石和金币。玩家当天获得的钥匙数量越多，开启宝箱拿到的奖励也越多。]],},
	[217] = {	type = [[狩猎场]],	title = [[狩猎场规则]],	subtitle = [[2.狩猎场]],	details = [[狩猎场共分为5个区域：]],},
	[218] = {	type = [[狩猎场]],	title = [[狩猎场规则]],	details = [[炉火山脉]],},
	[219] = {	type = [[狩猎场]],	title = [[狩猎场规则]],	details = [[NPC全部为火系精灵。可以掉落火之钥匙。]],},
	[220] = {	type = [[狩猎场]],	title = [[狩猎场规则]],	details = [[精灵瀑布]],},
	[221] = {	type = [[狩猎场]],	title = [[狩猎场规则]],	details = [[NPC全部为水系精灵。可以掉落水之钥匙。]],},
	[222] = {	type = [[狩猎场]],	title = [[狩猎场规则]],	details = [[绿野森林]],},
	[223] = {	type = [[狩猎场]],	title = [[狩猎场规则]],	details = [[NPC全部为草系精灵。可以掉落草之钥匙。]],},
	[224] = {	type = [[狩猎场]],	title = [[狩猎场规则]],	details = [[废弃电厂]],},
	[225] = {	type = [[狩猎场]],	title = [[狩猎场规则]],	details = [[NPC全部为电系精灵。可以掉落电之钥匙。]],},
	[226] = {	type = [[狩猎场]],	title = [[狩猎场规则]],	details = [[旱岩洞窟]],},
	[227] = {	type = [[狩猎场]],	title = [[狩猎场规则]],	details = [[NPC全部为岩系精灵。可以掉落岩之钥匙。]],},
	[228] = {	type = [[狩猎场]],	title = [[狩猎场规则]],	subtitle = [[3.属性克制]],	details = [[在狩猎场中的战斗，水克制火、火克制草、草克制岩、岩克制电、电克制水，攻击方克制防守方时，伤害获得额外30%的提升，防守方克制攻击方时，受到伤害减少50%。]],},
	[229] = {	type = [[狩猎场]],	title = [[狩猎场规则]],	subtitle = [[4.雇佣营地]],	details = [[1.玩家每天可以派遣最多2只精灵到雇佣营地中，其他玩家在狩猎场挑战时可以邀请这些精灵参战。]],},
	[230] = {	type = [[狩猎场]],	title = [[狩猎场规则]],	details = [[2.一个玩家只能邀请同一只精灵参战一次，一只精灵最多被邀请5次。]],},
	[231] = {	type = [[狩猎场]],	title = [[狩猎场规则]],	details = [[3.派遣的精灵每分钟可以获得一定的公会个人贡献，被邀请一次可以获得额外的个人贡献奖励。]],},
	[232] = {	type = [[狩猎场]],	title = [[狩猎场规则]],	details = [[4.派遣后不可手动召回，次日重置时自动召回，自动发放奖励，通过邮件提示。]],},
	[233] = {	type = [[符文]],	title = [[符文规则]],	details = [[每件装备上可以镶嵌4个符文。当前版本开放武器,头盔,戒指和衣服的符文镶嵌。]],},
	[234] = {	type = [[符文]],	title = [[符文规则]],	details = [[1号形状符文固定增加生命，2号形状符文固定增加攻击，3号形状符文固定增加防御，4号位置符文固定增加技能伤害。]],},
	[235] = {	type = [[符文]],	title = [[符文规则]],	details = [[每个位置只能镶嵌1个同样形状的符文。]],},
	[236] = {	type = [[符文]],	title = [[符文规则]],	details = [[符文有1~5星，1~4星分布在狩猎场的副本中，越后面的副本掉落的符文星级越高。星级高的符文属性数值更高。]],},
	[237] = {	type = [[符文]],	title = [[符文规则]],	details = [[5个区域的狩猎场掉落相应的5个套装所属的初始符文。当装备上镶嵌一定数量所属相同套装的符文时，会激活套装属性。（不同星级也可以激活）]],},
	[238] = {	type = [[符文]],	title = [[符文规则]],	details = [[符文使用魔力石升级。初始符文为白色，强化到一定等级后可以改变颜色。]],},
	[239] = {	type = [[符文]],	title = [[符文规则]],	details = [[升级所需的魔力石数量与等级有关，每次升级时根据当前等级，有固定的成功率。升级失败后会保留当前等级，不会降级。符文升级后，增加属性数值。+3，+6，+9，+12时会改变符文颜色，并且附加一项随机属性。]],},
	[240] = {	type = [[符文]],	title = [[符文规则]],	details = [[多余的符文可以通过分解，将符文转换成魔力石，给其他符文升级。]],},
	[241] = {	type = [[符文]],	title = [[符文规则]],	details = [[可以通过符文重生，消耗一定精灵石，返还70%消耗的魔力石。原符文变为初始状态。]],},
	[242] = {	type = [[公会弹劾]],	title = [[弹劾规则]],	subtitle = [[1.开启弹劾]],	details = [[当公会会长离线超过7天时，即可在公会成员界面点击公会会长对其进行弹劾。第一个成员点击后即可开启弹劾。]],},
	[243] = {	type = [[公会弹劾]],	title = [[弹劾规则]],	subtitle = [[2.投票]],	details = [[弹劾后，玩家可以对当前公会里其他成员进行投票，持续24小时，结束时得票最多的成员成为新一届会长。]],},
	[244] = {	type = [[公会弹劾]],	title = [[弹劾规则]],	details = [[当票数相同时，按照职位,再按照贡献，再按照等级进行排序。]],},
	[245] = {	type = [[公会弹劾]],	title = [[弹劾规则]],	subtitle = [[3.弹劾中止]],	details = [[当投票期间，原会长上线则取消弹劾。]],},
	[246] = {	type = [[羁绊]],	title = [[羁绊规则]],	subtitle = [[1.羁绊]],	details = [[在羁绊上的精灵可以激活队伍上精灵的羁绊。]],},
	[247] = {	type = [[羁绊]],	title = [[羁绊规则]],	details = [[第一个羁绊位置为免费，后续解锁需要消耗精灵石。]],},
	[248] = {	type = [[羁绊]],	title = [[羁绊规则]],	subtitle = [[2.羁绊共鸣]],	details = [[每个羁绊位置都可以为队伍上的精灵提供属性加成（每个位置提供单一的属性加成：攻击或生命）。]],},
	[249] = {	type = [[羁绊]],	title = [[羁绊规则]],	details = [[当羁绊位置上的精灵觉醒等级大于等于紫色时，即可激活属性加成。]],},
	[250] = {	type = [[羁绊]],	title = [[羁绊规则]],	details = [[每个位置的位置等级越高，觉醒等级越高，资质越高，属性转化率也越高。]],},
	[251] = {	type = [[羁绊]],	title = [[羁绊规则]],	details = [[最终加成在所有队伍精灵上的属性=位置上的精灵的相应属性*转化率]],},
	[252] = {	type = [[羁绊]],	title = [[羁绊规则]],	details = [[位置上的精灵的相应属性=该精灵的基础值+每级成长*精灵等级   不计算其他额外的加成]],},
	[253] = {	type = [[羁绊]],	title = [[羁绊规则]],	details = [[羁绊位置升级需要消耗金币，有一定几率会失败，使用精灵石可以提高成功的概率。]],},
	[254] = {	type = [[羁绊]],	title = [[羁绊规则]],	details = [[升级成功则提升位置等级，失败则保持当前等级不变。]],},
	[255] = {	type = [[限时探险]],	title = [[限时探险规则]],	details = [[每期限时探险都会出现2只稀有精灵。]],},
	[256] = {	type = [[限时探险]],	title = [[限时探险规则]],	details = [[在探险中战胜敌人，有几率获得精灵石、奇异石和稀有精灵的碎片。越后面的关卡难度越高，掉落高级奖励的几率也越大。]],},
	[257] = {	type = [[限时探险]],	title = [[限时探险规则]],	details = [[满足探险条件的精灵才可以进入。]],},
	[258] = {	type = [[限时探险]],	title = [[限时探险规则]],	details = [[每次探险消耗1张门票，每1小时恢复一张门票。当门票数量达到上限时，不再恢复。]],},
	[259] = {	type = [[限时探险]],	title = [[限时探险规则]],	details = [[3星通关的关卡再次进入时直接扫荡。]],},
	[260] = {	type = [[限时探险]],	title = [[限时探险规则]],	details = [[门票用完时，玩家可以付费购买门票，每天购买没有次数上限。]],},
	[261] = {	type = [[限时探险]],	title = [[限时探险规则]],	details = [[玩家可以使用奇异石兑换奖励。]],},
	[262] = {	type = [[限时探险]],	title = [[限时探险规则]],	details = [[也可以使用奇异石对两只稀有精灵进行召唤。如果召唤成功，则获得完整的一只稀有精灵，如果召唤失败，则不获得任何奖励，但下次召唤成功率提升。]],},
	[263] = {	type = [[探宝]],	title = [[探宝规则]],	subtitle = [[1.探宝]],	details = [[玩家22级解锁。派遣精灵进行探宝，在一定时间后可以获得金币奖励，还有一定概率获得意外惊喜哦！]],},
	[264] = {	type = [[探宝]],	title = [[探宝规则]],	details = [[在每个位置上都可以派遣一只精灵进行探宝。]],},
	[265] = {	type = [[探宝]],	title = [[探宝规则]],	details = [[共有3种探宝时间可以选择：1小时、3小时、8小时。]],},
